const plugin = require("tailwindcss/plugin");

module.exports = plugin(function ({ addComponents, theme }) {
addComponents({
  ".leo-dropdown": {
    "cursor": "pointer",
    "WebkitTapHighlightColor": "transparent"
  },
  ".leo-dropdown button": {
    "all": "unset"
  },
  ".leo-dropdown .click-target": {
    "flex": 1,
    "pointerEvents": "none"
  }
});
});