const plugin = require("tailwindcss/plugin");

module.exports = plugin(function ({ addComponents, theme }) {
addComponents({
  ".leo-progressbar": {
    "--radius": "var(--leo-progressbar-radius, 10px)",
    "--transition-duration": "var(--leo-progressbar-transition-duration, 0.2s)",
    "background": "var(--leo-progressbar-background-color, var(--leo-color-primary-20))",
    "height": "var(--leo-progressbar-height, 4px)",
    "borderRadius": "var(--radius)"
  },
  ".leo-progressbar .progress": {
    "height": "100%",
    "width": "100%",
    "transition": "width var(--transition-duration) ease-in-out",
    "background": "var(--leo-progressbar-color, var(--leo-color-button-background))",
    "borderRadius": "var(--radius)"
  }
});
});